ValidationRule.errorConditionFormula:
AND(
/*Check if clinical type is valid*/
OR(
AND(ISPICKVAL(Clinical_Type__c,"Healthcare Prime"),TEXT($User.Region__c)!=$Label.TerritoryRegionAMR),
AND(OR(ISPICKVAL(Clinical_Type__c,"IVD Partner"),ISPICKVAL(Clinical_Type__c,"Platform Distributor")),TEXT($User.Region__c)!=$Label.TerritoryRegionChina)
),
/*Fire validation rule on record creation and update*/
OR(ISCHANGED(Clinical_Type__c),ISNEW()),
/*Bypass criteria*/
NOT(
OR(
$Setup.cs_Apex_Settings__c.Bypass_All_Validations__c,
$Profile.Name = $Label.ProfileSystemAdmin,
$Profile.Name = $Label.ProfileDataSteward,
OR(ISCHANGED(Is_Updated_By_System__c),Is_Updated_By_System__c = true)
)
)
)
