ValidationRule.errorConditionFormula:
AND (
    NOT ( 
        ISBLANK ( Opportunity_Name__r.Lessor_Customer__c )
    ),
    RecordType.DeveloperName = $Label.CRFRecordTypeInstrument,
    OR (
        TEXT ( Opportunity_Name__r.Lessor_Customer__r.Account_Status__c ) = $Label.AccountStatusObsolete,
        TEXT ( Opportunity_Name__r.Lessor_Customer__r.Account_Status__c ) = $Label.AccountStatusPendingObsolete
    )
)
